import React, { useState } from "react";
import {
	StyleSheet,
	View,
	ScrollView,
	Text,
	Alert,
	Pressable,
	TextInput,
	Button,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";

const SignUpScreen = ({ navigation }) => {
	const [name, setName] = useState("");
	const [password, setPassword] = useState("");

	const handleName = (name) => {
		setName(name);
	};
	const handlePassword = (password) => {
		setPassword(password);
	};
	const handleSignIn = () => {
		if (name !== "" || password !== "") {
			Alert.alert(`Thank you for Signing In ${name}`);
			navigation.navigate("HomeScreen");
		} else {
			Alert.alert("Username and Password is not Correct");
		}
	};
	const handleSignUp = () => {
		navigation.navigate("HomeScreen");
	};

	return (
		<>
			<ScrollView>
				<View style={styles.dropView}>
					<Text style={styles.heading}>Sign-Up Screen</Text>
					<Icon name="paper-plane" size={40} color="white" />
				</View>
				<View style={styles.dropdown}>
					<Text style={styles.inputText}>Enter Your Name</Text>
					<TextInput style={styles.input} onChangeText={handleName} />
					<Text style={styles.inputText}>Enter Password</Text>
					
					<TextInput
						keyboardType="Number"
						style={styles.input}
						onChangeText={handlePassword}
					/>
				</View>
				<View style={styles.btnBackground}>
					<Text style={styles.inputText}>Agreed to Terms and Services</Text>
					<Pressable style={styles.btn} onPress={() => handleSignIn()}>
						<Text style={styles.btnText}>Sign Up</Text>
					</Pressable>
				</View>
			</ScrollView>
		</>
	);
};

const styles = StyleSheet.create({
	input: {
		height: 60,
		padding: 10,
		margin: 10,
		borderColor: "white",
		borderWidth: 1,
		borderRadius: 10,
		fontSize: 18,
		minWidth: 300,
	},
	inputText: {
		fontSize: 20,
		textAlign: "center",
		padding: 25,
		color: "white",
	},
	btnBackground: {
		backgroundColor: "#2ab6b1",
		color: "#333",
	},
	dropView: {
		flexDirection: "row",
		justifyContent: "space-between",
		alignItems: "center",
		padding: 15,
		backgroundColor: "#2ab6b1",
		borderBottomColor: "#eee",
		borderBottomWidth: 1,
	},
	dropdown: {
		flexDirection: "column",
		justifyContent: "center",
		alignItems: "baseline",
		padding: 15,
		backgroundColor: "#2ab6b1",
		borderBottomColor: "#eee",
		borderBottomWidth: 1,
	},
	heading: {
		fontSize: 20,
		color: "white",
	},
	btn: {
		backgroundColor: "#FFF",
		padding: 10,
		margin: 10,
		borderRadius: 10,
	},
	btnText: {
		fontSize: 20,
		color: "#333",
		textAlign: "center",
	},
});

export default SignUpScreen;
