import React, { useState } from "react";
import {
	StyleSheet,
	View,
	Text,
	Alert,
	Image,
	Pressable,
	TextInput,
	Button,
	ScrollView,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";

const BusDetailScreen = ({ navigation }) => {
	const [name, setName] = useState("");
	const [password, setPassword] = useState("");

	const handleSignUp = () => {
		navigation.navigate("SignUp");
	};

	return (
		<ScrollView>
			<View style={styles.container}>
				<Image
					source={require("../assets/bus2.jpg")}
					style={{
						width: 200,
						height: 200,
						resizeMode: "contain",
						borderRadius: 100,
						marginLeft: "25%",
					}}
				/>
				<Text style={styles.heading}>
					Bus Details <Icon name="bus" size={25} color="white" />
				</Text>
				<View style={styles.routeContainer}>
					<Text style={styles.route}>Route: </Text>
					<View style={styles.inputText}>
						<Text>9:00 to 9:10</Text>
						<Text>Quaidabad</Text>
					</View>
					<View style={styles.inputText}>
						<Text>10: to 10:10</Text>
						<Text>FAST</Text>
					</View>
				</View>
				<View style={styles.routeContainer}>
					<Text style={styles.booking}>Seat 1 Boarding Pass: 12</Text>
					<Text
						style={{
							borderBottomColor: "black",
							borderBottomWidth: StyleSheet.hairlineWidth,
						}}
					></Text>
					<Text style={styles.booking}>Seat 2 Boarding Pass: 15</Text>
				</View>
				<View style={styles.routeContainer}>
					<Text style={styles.booking2}>2 Seats Booked</Text>
				</View>
			</View>
		</ScrollView>
	);
};

const styles = StyleSheet.create({
	routeContainer: {
		display: "flex",
		flexDirection: "column",
		backgroundColor: "#fff",
		padding: 5,
		borderRadius: 10,
		marginTop: 30,
	},
	inputText: {
		fontSize: 16,
		fontWeight: "bold",
		padding: 5,
		color: "#333",
		display: "flex",
		flexDirection: "row",
		alignItems: "flex-start",
		justifyContent: "space-between",
		marginHorizontal: 55,
	},
	route: {
		fontSize: 18,
		fontWeight: "bold",
		textAlign: "left",
		padding: 5,
		color: "#333",
		borderColor: "#eee",
		borderWidth: 1.5,
	},
	container: {
		// justifyContent: "center",
		// alignItems: "center",
		flexDirection: "column",
		padding: 10,
		backgroundColor: "#2ab6b1",
		color: "#fff",
		borderColor: "white",
		borderBottomWidth: 1.5,
		width: "100%",
		height: "100%",
	},
	heading: {
		color: "white",
		fontSize: 30,
		textAlign: "center",
		fontWeight: "bold",
		marginBottom: 10,
		marginTop: 10,
	},
	booking: {
		textAlign: "center",
		fontSize: 20,
		marginTop: 15,
		marginBottom: 15,
	},
	booking2: {
		alignSelf: "center",
		textAlignVertical: "center",
		fontSize: 20,
		marginTop: 15,
		marginBottom: 15,
	},
});

export default BusDetailScreen;
