import React, { useState } from "react";
import {
	StyleSheet,
	View,
	Text,
	Alert,
	Image,
	Pressable,
	TextInput,
	Button,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";

const LoginScreen = ({ navigation }) => {
	const [name, setName] = useState("");
	const [password, setPassword] = useState("");

	const handleName = (name) => {
		setName(name);
	};
	const handlePassword = (password) => {
		setPassword(password);
	};
	const handleSignIn = () => {
		if (name !== "" || password !== "") {
			Alert.alert(`Good to have you back ${name}`);
			navigation.navigate("Maps")
		} else {
			Alert.alert("Username and Password is not Correct");
		}
	};
	const handleSignUp = () => {
		navigation.navigate("SignUp");
	};

	return (
		<>
			<View style={styles.container}>
				<Image
					source={require("../assets/bus1.jpg")}
					style={{
						width: 100,
						height: 100,
						resizeMode: "contain",
						borderRadius: 50,
						marginLeft: "35%",
					}}
				/>

				<Text style={styles.heading}>E-Ticketing App</Text>
				<Text style={styles.inputText}>Enter Your Name</Text>
				<TextInput style={styles.input} onChangeText={handleName} />
				<Text style={styles.inputText}>Enter Password</Text>
				<TextInput style={styles.input} onChangeText={handlePassword} />
				<Pressable style={styles.btn} onPress={() => handleSignIn()}>
					<Text style={styles.btnText}>Login</Text>
				</Pressable>

				<View style={styles.signUp}>
					{/* <Text>Don't have an account yet ? </Text> */}
					<Pressable style={styles.btn} onPress={handleSignUp}>
						<Text style={styles.btnText}>Sign Up</Text>
					</Pressable>
				</View>
			</View>
		</>
	);
};

const styles = StyleSheet.create({
	input: {
		height: 60,
		padding: 10,
		margin: 10,
		borderColor: "white",
		borderWidth: 1,
		borderRadius: 10,
		fontSize: 18,
		color: "white",
	},
	inputText: {
		fontSize: 20,
		textAlign: "left",
		padding: 15,
		color: "white",
	},
	container: {
		// justifyContent: "center",
		// alignItems: "center",
		flexDirection: "column",
		padding: 10,
		backgroundColor: "#2ab6b1",
		color: "#fff",
		borderColor: "white",
		borderBottomWidth: 1.5,
		width: "100%",
		height: "100%",
	},
	heading: {
		color: "white",
		fontSize: 30,
		textAlign: "center",
		fontWeight: "bold",
		marginBottom: 10,
		marginTop: 10,
	},
	btn: {
		backgroundColor: "white",
		padding: 10,
		margin: 10,
		borderRadius: 10,
	},
	btnText: {
		fontSize: 20,
		textAlign: "center",
		color: "#333",
	},
	checkbox: {
		margin: 8,
	},
});

export default LoginScreen;
